<?php/** * Template Name: Single Blog Post Page*//** * Description: BandOfArtists Individual Blog Post page * list one blog entry. * @since admired 1.0 */get_header(); ?>		<div id="primary">			<div id="content" role="main"> 				<header class="page-header">					<h1 class="page-title">          <span><a href="/">Home</a> > <a href="/blog/">Band of Artists Blog</a> > <?php the_title(); ?></span>          </h1> 				</header><?php while ( have_posts() ) : the_post(); ?><article id="post-<?php the_ID(); ?>" <?php post_class(); ?> ><?php if(the_post_thumbnail()) :?><div class="resource-img"><img src="<?php the_post_thumbnail(); ?>" alt="<?php the_title(); ?>"/></div><?php endif; ?><div class="resource-content">	<header class="entry-header">		<a href="<?php the_permalink();?>"><h1 class="entry-title"><?php the_title(); ?></h1></a>	</header><!-- .entry-header -->	<div class="entry-content" style="padding: 1.25em 0 0;">    <?php the_content(); ?>		<?php wp_link_pages( array( 'before' => '<div class="page-link"><span>' . __( 'Pages:', 'admired' ) . '</span>', 'after' => '</div>' ) ); ?>	</div><!-- .entry-content -->	<footer class="entry-meta" style="clear:both;"><?php				$utility_text = __( 'Bookmark the <a href="%3$s" title="Permalink to %4$s" rel="bookmark">permalink</a>.', 'admired' );			printf(				$utility_text,				esc_url( get_permalink() ),				the_title_attribute( 'echo=0' ),				get_the_author(),				esc_url( get_author_posts_url( get_the_author_meta( 'ID' ) ) )			);?>		<?php edit_post_link( __( 'Edit', 'admired' ), '<span class="edit-link">', '</span>' ); ?>	</footer><!-- .entry-meta --></div><!-- .resource-content--><div style="clear:both;"></div></article><!-- #post-<?php the_ID(); ?> -->  <?php endwhile;?>				</div><!-- #content -->		</div><!-- #primary --><?php get_sidebar(); ?>		<?php get_footer(); ?>